mapper.select:
select a.menu_code menuCode, a.menu_name menuName, a.is_deleted isDeleted, a.create_time createTime1, a.create_user createUser1, a.update_time updateTime1, a.update_user updateUser1, a.version from t_ysy_menu a where a.is_deleted = 0
and a.menu_code in ('6','7','9','10')
and a.menu_code in ('1321');
