mapper.select:
select a.shop_code shopCode, a.shop_name shopName, a.shop_tel shopTel, a.shop_address shopAddress, b.user_name userName, a.invite_code inviteCode, b.user_acct userAcct, a.is_deleted isDeleted, a.create_time createTime1, a.create_user createUser1, a.update_time updateTime1, a.update_user updateUser1, a.version from t_ysy_shop a, t_ysy_user b where a.is_deleted = 0 and a.user_code = b.user_code and b.role = 1
and a.user_code = (select user_code from t_ysy_shop where user_code = #{userId})
and a.shop_code like concat('%', #{shopCode}, '%')
and a.shop_name like concat('%', #{shopName}, '%')
and b.user_name like concat('%', #{userName}, '%')
and a.province_code = #{provinceCode}
and a.city_code = #{cityCode}
and a.area_code = #{areaCode}
order by a.create_time desc
